import  React, {useState} from "react";
function searchWordList (prop) {
    prop.preventDefault();
  let wordLookFor = prop.wordInputSearch;
  let listWord = prop.listItems;
  for (let i = 0; i < listWord.length; ++i) {
       if (listWord[i] === wordLookFor) {
        this.setState({
          text: 'Word exist in the dictionary',
        })
         break;
       } else {
        this.setState({
          text:'Word dont exist in the dictionary',
        })
       }
      }
     }  

function SearchW(_props) {
    const [wordInputSearch, wordSearch] = useState("");
    return  <form onSubmit={searchWordList}>Look for the word   
        <input value={wordInputSearch} onChange={wordSearch}/>
       <button type="submit">Search</button>
       </form>
}

export default SearchW;
