import React from "react";
import { useState } from 'react';


function AddList(prop) {
  const [state, setState] = React.useState({ first: wordInputAddList, second: wordNew});

  function addWordList(props) {
    props.preventDefault()
    let listWord = props.listItems;
      let wordAdd = props.wordInputAddList;
    if (listWord.length === 0 && wordAdd !== '') {
          this.setState({
      listItems: [...props.listItems, props.wordInputAddList],
      wordInputAddList:''
    })
    } else {
      for (let j = 0; j < listWord.length; ++j) {
           if (listWord[j] === wordAdd) {
            alert("The word is in the dictionary");
            break;
          } else if (listWord[j] !== wordAdd && wordAdd !== '') {
            this.setState({
            listItems: [...props.listItems, props.wordInputAddList],
            wordInputAddList:''
          })
        }
      }
    }
    }   
   return  <from  onSubmit={addWordList}> Word  
     <input value={wordInputAddList} onChange={wordNew}/>
     <button type="submit">Add word</button>
     </from> 
}

export default AddList;
