import React, { useState} from "react";
import {dicti} from "./addElementList";
function SearchW() {
  let dictionar = dicti;
  let mesaj;
  const [searchWord, setSearchWord] = useState(''); 
  const WordSearch = (e) => {
    e.preventDefault();
    alert(searchWord);
    for (let i = 0; i < dictionar.length; ++i) {
        if (dictionar[i].addWord === searchWord) {
     mesaj = "Este in dicitonar";
        }
        else {
          mesaj = "Nu este in dictionar";
        }
    }
  } 
    return <><form onSubmit={WordSearch}>Look for the word
      <input name='searchWord' value={searchWord} onChange={(e) => setSearchWord(e.target.value)} />
      <button> Search </button>
    </form><div>{mesaj}</div></>
}
export default SearchW;
