import  React, {useState} from "react";

function addWordList(prop){
  prop.preventDefault()
    let listWord = prop.listItems;
    let wordAdd = prop.wordInputAddList;
  if (listWord.length === 0 && wordAdd !== '') {
        this.setState({
    listItems: [...prop.listItems, prop.wordInputAddList],
    wordInputAddList:''
  })
  } else {
    for (let j = 0; j < listWord.length; ++j) {
         if (listWord[j] === wordAdd) {
          alert("The word is in the dictionary");
          break;
        } else if (listWord[j] !== wordAdd && wordAdd !== '') {
          this.setState({
          listItems: [...this.state.listItems, this.state.wordInputAddList],
          wordInputAddList:''
        })
      }
    }
  }
  }   
function AddList(prop) { 
  const [wordInputAddList, wordNew] = useState("");
   return <><from onSubmit={addWordList}> Word
     <input value={wordInputAddList} onChange={wordNew} />
     <button type="submit">Add word</button>
   </from><br></br></>
    {prop.listItems.map((li, key) => <li {...{ key }}>{li}</li>)}
    <br></br>
}

export default AddList;
