import './App.css';
import AddList from './component/addElementList';
import Title from './component/title';
import SearchW from './component/searchWord';



function App (props) {
    this.state = {
    listItems: [],
    wordInputAddList:'',
    wordInputSearch:'',
    text:'',
  }

function  wordSearch () {
  this.setState({
wordInputSearch: props
});
}
function searchWordList () {
  props.preventDefault();
let wordLookFor = props.wordInputSearch;
let listWord = props.listItems;
for (let i = 0; i < listWord.length; ++i) {
     if (listWord[i] === wordLookFor) {
      this.setState({
        text: 'Word exist in the dictionary',
      })
       break;
     } else {
      this.setState({
        text:'Word dont exist in the dictionary',
      })
     }
    }
   }  

return (
   <><p>
    <Title />
    <AddList />
    <SearchW />
  </p><div>
  {
        props.listItems.map((li,key) => <li {...{key}}>{li}</li>)
    }

      <h2>{props.text}</h2>
    </div></>
);
}
export default App;
