import './App.css';
class App extends Component {
  constructor(props) {
    super(props);
   this.state = {
    listItems: [],
    wordInputAddList:'',
    wordInputSearch:'',
    text:'',
  }
}
  wordNew = ({target:{value}}) => this.setState({
    wordInputAddList: value
  })

addWordList = e =>{
e.preventDefault()
let listWord = this.state.listItems;
  let wordAdd = this.state.wordInputAddList;
if (listWord.length === 0 && wordAdd !== '') {
      this.setState({
  listItems: [...this.state.listItems, this.state.wordInputAddList],
  wordInputAddList:''
})
} else {
  for (let j = 0; j < listWord.length; ++j) {
       if (listWord[j] === wordAdd) {
        alert("The word is in the dictionary");
        break;
      } else if (listWord[j] !== wordAdd && wordAdd !== '') {
        this.setState({
        listItems: [...this.state.listItems, this.state.wordInputAddList],
        wordInputAddList:''
      })
    }
  }
}
}   
wordSearch = ({target:{value}}) => this.setState({
wordInputSearch: value
})

searchWordList = e =>{
e.preventDefault()
let wordLookFor = this.state.wordInputSearch;
let listWord = this.state.listItems;
for (let i = 0; i < listWord.length; ++i) {
     if (listWord[i] === wordLookFor) {
      this.setState({
        text: 'Word exist in the dictionary',
      })
       break;
     } else {
      this.setState({
        text:'Word dont exist in the dictionary',
      })
     }
    }
   }  
render() {
return (
  <div>
    <h1>Dictionary</h1>
    <br></br>
    <form onSubmit={this.addWordList}>Word  
      <input value={this.state.wordInputAddList} onChange={this.wordNew} />
      <button type="submit">Add word</button>
    </form>
    <br></br>
    <form onSubmit={this.searchWordList}>Look for the word   
      <input value={this.state.wordInputSearch} onChange={this.wordSearch}/>
      <button type="submit">Search</button>
    </form>

    <h4>
    {
        this.state.listItems.map((li,key) => <li {...{key}}>{li}</li>)
    }
    </h4>
    <h2>{this.state.text}</h2>
  </div>
)
}
}
export default App;
