import React, { useState } from "react";
import { useBetween } from 'use-between';
function AddList() { 
  const [addWord, setAddWord] = useState(''); 
  const [list, setList] = useBetween(useShareableState);


  function listWord(e) {
    e.preventDefault();
    let word = { addWord };
    if (list.length > 0) {
      for (let i = 0; i < list.length; ++i) {
        if (Object.keys(list[i]).length === Object.keys(word).length
          && Object.keys(list[i]).every(p => list[i][p] === word[p])) {
          alert('este in lista');
        } else {
          setList([...list, { addWord }]);
        }
      }
    } else {
      setList([...list, { addWord }]);
    }

  }
   return <div>
   <form onSubmit={listWord}> Word
    <input name ='addWord' value ={addWord} onChange={(e) => setAddWord(e.target.value)}/>
     <button> Add word </button>
   </form>
   <div> {
     list.map((list)=><p>
       <li>{list.addWord}</li>
     </p>)
   }</div>
   </div>
}

export default AddList;
