function AddList(props) { 
const [wordNew, wordInputAddList] = useState(" ");
const [listItems] = useState(" ");
const addWordList = () => {
  prop.preventDefault()
    let listWord = listItems;
    let wordAdd = props.wordInputAddList;
  if (listWord.length === 0 && wordAdd !== '') {
        this.setState({
    listItems: [...listItems, wordInputAddList],
    wordInputAddList:''
  })
  } else {
    for (let j = 0; j < listWord.length; ++j) {
         if (listWord[j] === wordAdd) {
          alert("The word is in the dictionary");
          break;
        } else if (listWord[j] !== wordAdd && wordAdd !== '') {
          this.setState({
          listItems: [...listItems, wordInputAddList],
          wordInputAddList:''
        })
      }
    }
  }
  }   
   return <from> Word
     <input value={wordInputAddList} onChange={(e) => {wordNew(e.target.wordInputAddList)}} />
     <button type="submit" onClick={addWordList}>Add word</button>
   </from>
  {listItems.map((li, key) => {
    return <li {...{ key }}>{li}</li>;
  })}
}

export default AddList;
